    private void enterButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enterButtonActionPerformed
        // delcaring the string variables
        String name, password; 
        name = usernameText.getText().toLowerCase();
        password = passwordText.getText().toLowerCase(); 
        // the if statement which checks if he password has 8 characters
        
        if (password.length() < 8) {
            outputLabel.setText("What the hecking frick frack are you tryna pull here");
        }
        else {
            outputLabel.setText("Your user is: " + name + ", and your password is: " + password+randNum);
        }
            
                
        
        
        
        
        
        
    }//GEN-LAST:event_enterButtonActionPerformed

