    private void enterButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enterButton2ActionPerformed
        // some more string definitions
        String name, password, nameSurprise, passwordSurprise;
        name = usernameText.getText().toLowerCase();
        password = passwordText.getText().toLowerCase();
        nameSurprise = secretText.getText();
        passwordSurprise = secretPassText.getText();
        // another if statement for your secret message
        if (nameSurprise.equals(name) && passwordSurprise.equals(password+randNum)){
            surpriseLabel.setText("You cheated, didn't you?");
        }
        else {
            surpriseLabel.setText("Lol go away");
        }
    }//GEN-LAST:event_enterButton2ActionPerformed

